export default function Sidebar() {
  const path = usePathname();
  console.log(path);
  return (
    <div className="w-[152px] h-screen bg-[#050505] flex flex-col">
      {/* Logo */}
      <div className="flex items-center justify-center p-4 h-[88px]">
        <img
          src="https://api.builder.io/api/v1/image/assets/TEMP/d6d6d01df6abba2c2b4b58e4d5f79e8914262ec5?width=268"
          alt="Decenter AI Logo"
          className="w-[133px] h-[53px]"
        />
      </div>

      {/* Divider */}
      <div className="w-full h-[1px] bg-[#191919]"></div>

      {/* Navigation */}
      <div className="flex flex-col py-20 gap-7">
        {navigationItems.map((item, index) => (
          <Link
            key={index}
            href={item.href}
            className={`flex flex-col items-center py-2 px-9 gap-2 cursor-pointer transition-colors ${
              path === item.href ? "bg-[#191919]" : "hover:bg-[#191919]/50"
            }`}
          >
            <div className="flex flex-col items-center gap-2">
              <div className="w-6 h-6 flex items-center justify-center">
                {item.icon}
              </div>
              <span
                className={`text-base font-normal leading-6 ${
                  path === item.href ? "text-[#F5F5F5]" : "text-[#5D5D5D]"
                }`}
              >
                {item.name}
              </span>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
